import React, { useState } from "react";
import { API_URL } from "../../constants";
import { useNavigate } from "react-router-dom";



import './login.css';
import Shake from "../shake/shake";

const Login = ({ setIsLoggedIn }) => {
    const[username, setUsername1]=useState("");
    const[password, setPassword]=useState("");
    const[userIsActive, setUserIsActive]=useState("");

    const handleLogin = () => {
        let item={username,password};

        fetch(`${API_URL}/auth/sign-in`, {
            method:"POST",
            headers:{
                "Content-Type": "application/json",
                "Accept": "application/json"
            },
            body: [JSON.stringify(item)]
        }).then((res) => res.json()).then((res) => {
            if(userIsActive){
                Shake.login();
            localStorage.setItem("token",JSON.stringify(res))
            setIsLoggedIn(true);
            navigate("/shake")

                                    }
            else if(username==="User_2"){
                localStorage.setItem("token",JSON.stringify(res))
                setIsLoggedIn(true)

            }
        })
}


return (
    <div className="wrapper">
        <h1>Login page</h1>
            <input type="text"
                   placeholder="Username"
                   onChange={(e) => setUsername1(e.target.value) }
                   className="form-control"/>
            <input type="password"
                   placeholder="Password"
                   onChange={(e) => setPassword(e.target.value)}
                   className="form-control"/>
        <button onClick={handleLogin} className="btn-submit">Login</button>
    </div>
    );
}

export default Login




function useLogoutTimer() {
  const userIsInactive = useFakeInactiveUser();
  const navigate = useNavigate();

  useEffect(() => {
    if (userIsInactive) {
      fake.logout();
      navigate("/session-timed-out");
    }
  }, [userIsInactive]);
}
